tinymce.PluginManager.requireLangPack("mc_news");tinymce.PluginManager.add("mc_news",function(editor,url){function showDialog(){var win,data={},dom=editor.dom;win=editor.windowManager.open({title:"mc_news Title",file:tinyMCE.baseURL+"/plugins/mc_news/news.php",width:800,height:550,inline:1,resizable:true,maximizable:true})}editor.addButton("mc_news",{icon:"fa far fa-newspaper",tooltip:"mc_news Title",onclick:showDialog,onPostRender:function(){var ctrl=this;editor.on("NodeChange",function(e){ctrl.active(e.element.nodeName=="IMG")})}});editor.addMenuItem("mc_news",{text:tinymce.util.I18n.translate("mc_news menu"),icon:"fa fas fa-newspaper",onclick:showDialog,context:"insert"})});