+function($){"use strict";$(window).on("load",function(){let tar=window.innerHeight/2;function affixHead(){let pos=window.pageYOffset,atTop=$("#header").hasClass("at-top");if(pos>tar&&atTop){$("#header").removeClass("at-top");$("body > .toTop").addClass("affix")}else if(pos<tar&&!atTop){$("#header").addClass("at-top");$("body > .toTop").removeClass("affix")}}$(window).scroll(affixHead);$(window).resize(affixHead);affixHead()})}(jQuery);